const response = ($input.all()[0] || {}).json;

// If we fetched by slug, the Production API returns a single player object.
// If we fetched by name, it returns { results: [...] }.
const players = response && typeof response === 'object'
  ? (Array.isArray(response.results) ? response.results : (response.slug ? [response] : []))
  : [];

if (players.length === 0) {
  return [{
    json: {
      found: false,
      message: 'Player not found on production'
    }
  }];
}

// If name search returned multiple players, require slug to avoid mass-updates.
if (Array.isArray(response.results) && players.length !== 1) {
  return [{
    json: {
      found: false,
      message: `Multiple players found (${players.length}). Provide slug to update safely.`
    }
  }];
}

const player = players[0];

return [{
  json: {
    found: true,
    id: player.id,
    name: player.name,
    slug: player.slug,
    club_name: player.club_name,
    position: player.position,
    nationality: player.nationality,
    current_summary: player.summary
  }
}];

const wikiData = $json || {};
const playerData = ($node["IF Player Found"] && $node["IF Player Found"].json) ? $node["IF Player Found"].json : {};

let hasData = false;
let extract = '';

if (wikiData.query && wikiData.query.pages) {
  const pages = wikiData.query.pages;
  const pageId = Object.keys(pages)[0];

  if (pageId !== '-1') {
    const page = pages[pageId];
    extract = page.extract || '';
    hasData = extract.length > 50;
  }
}

return {
  id: playerData.id,
  name: playerData.name,
  slug: playerData.slug,
  club: playerData.club_name,
  position: playerData.position,
  nationality: playerData.nationality,
  wikipedia_extract: extract,
  has_wiki_data: hasData
};

