parameters.jsCode:
const geminiResponse = $json || {};
const playerData = ($node["Process Wikipedia Data"] && $node["Process Wikipedia Data"].json) ? $node["Process Wikipedia Data"].json : {};

function fallbackSummary() {
  const name = playerData.name || 'Ten zawodnik';
  const club = playerData.club || playerData.club_name || 'tego klubu';
  const nationality = playerData.nationality ? ` (${playerData.nationality})` : '';
  const position = playerData.position ? `, pozycja: ${playerData.position}` : '';

  const sentences = [];
  sentences.push(`${name}${nationality} w barwach ${club}${position} to piłkarz z kategorii: jak już ruszy, to stadion robi oho.`);
  sentences.push('Potrafi w pół sekundy przejść od co on robi do ok, to było dobre i z powrotem, więc rywale nie są jedynymi, którzy gubią się w jego pomysłach.');
  sentences.push('Najlepiej czuje się, gdy ma miejsce na przyspieszenie i drybling, bo wtedy jeden ruch potrafi zrobić więcej niż trzy schematy.');
  sentences.push('A gdy nic nie wychodzi, i tak ma aurę zawodnika, który w 89. minucie przypomni, że jest po to, żeby działo się cokolwiek.');
  sentences.push('Ekstraklasa lubi takie postacie: nie zawsze jest liczba, ale prawie zawsze jest historia.');

  return sentences.join(' ');
}

let summary = '';

try {
  if (geminiResponse.candidates && geminiResponse.candidates[0]) {
    const text = geminiResponse.candidates[0].content.parts[0].text;
    const jsonText = String(text).replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    const parsed = JSON.parse(jsonText);
    summary = parsed.summary || '';
  }
} catch (e) {
  // ignore and fallback
}

if (!summary || String(summary).trim().length < 20) {
  summary = fallbackSummary();
}

return {
  id: playerData.id,
  name: playerData.name,
  slug: playerData.slug,
  summary: summary
};

const allResults = $input.all();

if (allResults.length === 0) {
  return [{
    json: {
      status: 'success',
      count: 0,
      players: []
    }
  }];
}

const players = allResults.map(item => {
  const data = item.json;
  return {
    name: data.name || 'Unknown',
    slug: data.slug || 'unknown',
    summary: data.summary ? (data.summary.length > 100 ? data.summary.substring(0, 100) + '...' : data.summary) : 'No summary'
  };
});

return [{
  json: {
    status: 'success',
    count: players.length,
    players
  }
}];
